(function(){const head=document.head||document.getElementsByTagName('head')[0];if(!document.querySelector('meta[charset]')){const metaCharset=document.createElement('meta');metaCharset.setAttribute('charset','utf-8');document.head.prepend(metaCharset);}const metaVp=document.createElement('meta');metaVp.name="viewport";metaVp.content="width=device-width,initial-scale=1.0";document.head.appendChild(metaVp);const fontLink=document.createElement('link');fontLink.rel='stylesheet';fontLink.href='https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@100..900&family=Poppins:wght@100..900&display=swap';document.head.appendChild(fontLink);const style=document.createElement('style');style.textContent=`*{font-family:'Poppins','Noto Sans JP',sans-serif;box-sizing:border-box;}body{margin:0;padding:2rem 1.5rem;line-height:1.8;color:#333;background-color:#fcfcfc;white-space:pre-wrap;word-wrap:break-word;display:block!important;}h1{font-size:1.6rem;font-weight:700;border-left:5px solid #333;padding:0.2rem 0 0.2rem 0.8rem;margin:0 0 2rem 0;line-height:1.3;white-space:normal;}`;document.head.appendChild(style);const formatContent=()=>{const body=document.body;const tempBody=body.cloneNode(true);const scripts=tempBody.querySelectorAll('script');scripts.forEach(s=>s.remove());const rawText=tempBody.innerText.trim();if(!rawText)return;const lines=rawText.split('\n');const firstLine=lines[0].trim();document.title=firstLine;const mainText=lines.slice(1).join('\n');body.innerHTML=`<h1>${firstLine}</h1>${mainText}`;};if(document.readyState==='loading'){window.addEventListener('DOMContentLoaded',formatContent);}else{formatContent();}})();
